import {Container, Row, Col, Table} from 'reactstrap';
import Axios from 'axios';


export default function Coin() {

    const [coins, setCoin] = useState([]);

    useEffect(() => {
        return () => {
            Axios.get("https://api.coingecko.com/api/v3/coins/makets?vs_currency=usd&order=market_cap_desc&per_page=30&page=1&sparkline=false")
                .then((res)=>{
                    console.log(res.data);
                    setCoin(res.data);
                    })
                .catch(()=> console.error("API didn't come !!!"))
        }
    },[])
    return (
        <Container>
