import React, {useState, useEffect} from 'react';
import {Container, Row, Col, Table} from 'reactstrap';
import Axios from 'axios';


export default function Coin() {

    const [coins, setCoins] = useState([]);

    useEffect(() => {
        Axios.get("https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=30&page=1&sparkline=false")
            .then((res)=>{
                setCoins(res.data);
          }).catch(error=> console.error("API didn't come !!! ", error)) 
    },[]);

    return (
        <Container>
            <Row>
                <Col md="12">
                    <Table className=" text-light">
                        <thead>
                            <tr>
                            <th>â„–</th>
                            <th>Crypto Name  </th>
                            <th>Current Crypto Price</th>
                            <th>Market Cap Change 24h</th>
                            </tr>
                        </thead>
                        
                        <tbody>
                            {coins.map((coin, i) => {
                                return( 
                                    <tr key={coin.id}>
                                        <th scope="row">{++i}</th>
                                        <td>
                                            <img src={coin.image} alt={coin.name + "-logo"} width="50px" className="mr-4"/>
                                            {coin.name}
                                        </td>
                                        <td>{coin.current_price}$</td>
                                        {
                                            (coin.market_cap_change_percentage_24h < 0)? 
                                            (<td className="text-danger">{coin.market_cap_change_percentage_24h.toFixed(2)}%</td>) 
                                            :
                                            (<td className="text-success">{coin.market_cap_change_percentage_24h.toFixed(2)}%</td>) 
                                        }
                                        
                                    </tr>
                                )
                            })}
                        </tbody>
                    </Table>
                </Col>
            </Row>
        </Container>
    )
}
